import React, { useContext } from "react";
import { ShopContext } from "../../context/shop-context";

// Create a function to dynamically import images
function importAll(r) {
  let images = {};
  r.keys().forEach((item) => {
    images[`./assets${item.replace(".", "")}`] = r(item);
  });
  return images;
}

// Import all webp images from assets folder
const images = importAll(require.context("../../assets", false, /\.(webp)$/));

export const Product = (props) => {
  const { id, productName, price, productImage } = props.data;
  const { addToCart, cartItems } = useContext(ShopContext);
  const cartItemCount = cartItems[id];

  return (
    <div className="product">
      <img src={images[productImage]} alt={productName} />

      <div className="description">
        <p>
          <b>{productName}</b>
        </p>
        <p> ${price}</p>
      </div>
      <button className="addToCartBttn" onClick={() => addToCart(id)}>
        Add To Cart {cartItemCount > 0 && <> ({cartItemCount})</>}
      </button>
    </div>
  );
};
