import React from "react";
import { Product } from "./product";
import "./shop.css";
import { useEffect, useState } from "react";
import axios from "axios";

export const Shop = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:3001/api/products");
        // Modify the image paths to point to public folder
        const productsWithCorrectPaths = response.data.map(product => ({
          ...product,
          productImage: `/images/${product.productImage.split('/').pop()}`  // Convert "./assets/1.png" to "/images/1.png"
        }));
        setProducts(productsWithCorrectPaths);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className="shop">
      <div className="shopTitle">
        <h1>Tech Shop</h1>
      </div>
      <div className="products">
        {products.map((product) => (
          <Product key={product._id} data={product} />
        ))}
      </div>
    </div>
  );
};
